// Написати функцію, яка приймає 1 параметр. з тим, що передали перший раз і т. д.
// Все це із замиканнями, наприклад: sum(3) = 3 sum(5) = 8 sum(20) = 28


function lockedFunction () {
  let sumlockedFunction = 0;

  return function (lockedInFunction) {
    sumlockedFunction += lockedInFunction;
    return sumlockedFunction;
  }

}

const viewLockedSum = lockedFunction();

console.log(viewLockedSum(3));

console.log(viewLockedSum(5));

console.log(viewLockedSum(20));